function showMsgBox(Params) { /*(Params = {Position:{Top:...,Left:...}, 											(Facultatif (non renseign√© par d√©faut) : Permet le positionnement personnalis√© de la boite de message.)
										  Type:"Alert/Confirm",  												(Facultatif ('Alert' par d√©faut) : ('Alert' = un seul bouton √† param√©trer "Ok", 'Confirm' = 2 boutons "Ok" "Cancel")
										  Title:"...",  														(Titre de la boite de dialogue)
										  Content:"...", 														(Contenu)
										  Ok:{Title:"...",fn:function({...;return true/false;})},				(Title=Libell√©, fn=fontion √† ex√©cuter lors du click suivi de return true=fermeture de la boite de dialogue/false=pas de fermeture (fn facultatif))
										  Cancel:{Title:"...",fn:function({...;return true/false;})}			(Title=Libell√©, fn=fontion √† ex√©cuter lors du click suivi de return true=fermeture de la boite de dialogue/false=pas de fermeture (fn facultatif))
										  })*/
    // Cr√©ation de la DIV qui sert de base √† la boite de dialogue 
    var index = $('.ui-dialog').size();
    var $Dlg = $(document.createElement('div'));
    $Dlg.attr('id', 'Dialog' + index)
		.addClass('Dialog');
    $().append($Dlg);


    // Pr√©partion des boutons
    var dlg_btns = {};
    if (Params.Type == 'Confirm') { dlg_btns['CANCEL'] = { 'text': Params.Cancel.Title, 'class': 'CANCEL', 'click': function () { if (Params.Cancel.fn != undefined) { if (Params.Cancel.fn()) { $(this).dialog('close'); } } else { $(this).dialog('close'); }; } } }
    dlg_btns['OK'] = { 'text': Params.Ok.Title, 'class': 'OK', 'click': function () { if (Params.Ok.fn != undefined) { if (Params.Ok.fn()) { $(this).dialog('close'); } } else { $(this).dialog('close'); }; } };

    // Positionnement
    Params.Position = (Params.Position == undefined) ? {} : Params.Position;
    var Position = [(Params.Position.Left == undefined) ? 'center' : Params.Position.Left, (Params.Position.Top == undefined) ? 'center' : Params.Position.Top]

    // Largeur
    Params.Width = (Params.Width == undefined) ? 448 : Params.Width;

    // Contenu du message 
    $Dlg.append(Params.Content);


    // Affichage de la boite de dialog
    $Dlg.dialog({
        dialogClass: Params.Type,
        title: Params.Title,
        buttons: dlg_btns,
        closeText: '',
        closeOnEscape: true,
        modal: true,
        resizable: false,
        minHeight: 250,
        position: Position,
        width: Params.Width,
        open: function (type, data) {
            zIndex = 11000 + index * 2;
            $('.ui-widget-overlay').css({ 'z-index': zIndex, 'height': $('#formAspNet').outerHeight() });
            $('.ui-dialog').css('z-index', zIndex + 1);
            $Dlg.find('a').blur()
                .find('button').blur()
                .find('.OK').focus();
        },
        dragStop: function (event, ui) {
            $("#dialog-modal").dialog({ position: 'center', draggable: true });
        }
    }).on('dialogclose', function (event) { $Dlg.dialog('destroy'); $Dlg.remove(); });
}

