var tm_Loader;
var delay_Loader = "250"; // d√©lai d'apparition du Loader
function AjaxCall(P) {/*(P = {Async:true/false, 			(Facultatif (true par d√©faut) : Traitement synchrone(false) ou asynchrone(true))
							 Type:'html'/'script'		(Facultatif ('html' par d√©faut) : Indique le type de donn√©es attendues ('html' = d√©faut) 
							 Url:"",  					(url a appel√©e)
							 Data:{...},  				(Facultatif (non renseign√© par d√©faut) : Tableau de param√®tres √† passer √† l'url ex: {idprog:x,idmod:y})
							 Container:$(...), 			(Facultatif (non renseign√© par d√©faut) : Si Type = 'html' permet d'ins√©rer le contenu dans l'√©l√©ment Container ex: $('#Resultat'))
							})*/
    $.ajax({
        async: (P.Async == undefined) ? true : P.Async,
        type: "POST",
        cache: false,
        url: P.Url,
        data: P.Data,
        dataType: (P.Type == undefined) ? 'html' : P.Type,
        statusCode: {
            404: function () { showMsgBox({ Type: 'Alert', Title: 'Attention', Ok: { Title: 'Ok' }, Content: 'page inconnue.' }); },
            //500: function () { showMsgBox({ Type: 'Alert', Title: 'Attention', Ok: { Title: 'Ok' }, Content: 'Erreur serveur' }); }
            500: function (xhr) { showMsgBox({ Type: 'Alert', Title: 'Attention', Ok: { Title: 'Ok' }, Content: 'Erreur serveur :<br/><br/>' + xhr.responseText }); }
        }
    })
	.done(function (content) {
	    if (P.Type == "html") {
	        if (content.indexOf('showMsgBox') != -1) {
	            eval(content);
	        } else {
	            P.Container.html(content);
	            if (P.fn != undefined) { P.fn(); }
	        }
	    } else { if (P.fn != undefined) { P.fn(); } };
	})
	.always(function (content) { clearTimeout(tm_Loader); $('#ajaxLoading').remove(); $('#ajaxLoader').remove(); });
}

