// Redimensionnement du panneau gauche et de la zone de contenu
scrollpnl_Resize = function (P) {
    P.H = (P.H == undefined) ? wH : P.H;
    P.W = (P.W == undefined) ? cW : P.W;
    if (P.H != wH) {
        wH = P.H;
        $LeftScroll.height(wH - 148 - $LeftPanel.children('#title_Panel').outerHeight());
        $Content.height(wH - 208);
    }
    if (P.W != cW) {
        $LeftPanel.css({ 'overflow': 'hidden' });
        overflow = (P.W == 0) ? 'hidden' : '';
        $Summary.animate({ opacity: 0 }, 100);
        $LeftPanel.animate({ width: P.W }, 150, function () {
            $LeftPanel.css({ 'overflow': overflow })
            $Summary.find('#Root > li').each(function () {
                $LI = $(this);
                if ($LI.hasClass('Opened')) {
                    $LI.css({ height: $LI.children('h2').outerHeight() + $LI.children('ul').outerHeight() + 5 });
                } else {
                    $LI.css({ height: $LI.children('h2').outerHeight() });
                }
            });
            $Summary.animate({ opacity: 1 }, 100);
        });
        $Content.animate({ width: $Content.outerWidth() + (cW - P.W) }, 150);
        cW = P.W;
    }
    if (cW == pWMax) { $LeftPanel.addClass("Max"); }
    else { $LeftPanel.removeClass("Max"); }
    $LeftScroll.mCustomScrollbar("update");
    $ScrollResource.mCustomScrollbar("update");
}

