// gestion du changement de contenu du panneau gauche 
ges_LeftPanel = function (ContentType) {
    pnl_switch = !$LeftPanel.hasClass(ContentType);
    $Resource.unhighlight();
    $LeftPanel.removeClass()
              .addClass(ContentType);

    if (ContentType == "Summary") {
        currentPrefix = "S_";
        $LeftPanel.children('#title_Panel').html(lngTitleSummary);
    } else if (ContentType == "SearchResults") {
        currentPrefix = "R_";
        $LeftPanel.children('#title_Panel').html(lngTitleSearchResults.replace("{0}", $SearchResults.children('ul').children('li').size()));
    } else if (ContentType == "NotesBookmarks") {
        currentPrefix = "N_";
        $LeftPanel.children('#title_Panel').html(lngTitleNotesBookmarks);
    }

    if ((ContentType != "SearchResults") && !pnl_switch && (cW > 0)) {
        // si le panneau est ouvert on le ferme
        $LeftScroll.animate({ opacity: 0 }, 80);
        scrollpnl_Resize({ W: 0 });
    }
    else {
        // sinon on l'ouvre 
        wH = 0;//force le redimensionnement en hauteur du panneau gauche
        scrollpnl_Resize({ H: get_maxHeight(), W: (cW == 0) ? pWMin : cW })
        if ($LeftScroll.css('opacity') == 0) { $LeftScroll.animate({ opacity: 1 }, 150); }
